import React, { useState } from 'react';
import Question from './components/Question';
import Result from './components/Result';
import './App.css';

import questions from './questions.json';

// üí° Î∞±ÏóîÎìú ÏÑúÎ≤ÑÏùò Ï†àÎåÄ URLÏùÑ ÏÉÅÏàòÎ°ú Ï†ïÏùò
const BACKEND_URL = 'http://54.180.90.210:32000'

function App() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [scores, setScores] = useState({ T: 0, E: 0 });
  const [gender, setGender] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState(null); // To store result from backend

  const handleAnswer = (type, value) => {
    if (type === 'gender') {
      setGender(value);
      setCurrentQuestion(currentQuestion + 1);
      return;
    }

    const newScores = { ...scores, [type]: scores[type] + 1 };
    setScores(newScores);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Last question answered, call API
      fetch(`${BACKEND_URL}/result`, { // üí° ÏàòÏ†ïÎêú Î∂ÄÎ∂Ñ: Ï†àÎåÄ Í≤ΩÎ°ú ÏÇ¨Ïö©
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ...newScores, gender }),
      })
      .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setResultData(data);
        setShowResult(true);
      })
      .catch(error => {
        console.error('Error fetching result:', error);
        // ÏóêÎü¨ Î∞úÏÉù Ïãú ÏÇ¨Ïö©ÏûêÏóêÍ≤å ÏóêÎü¨ Î©îÏãúÏßÄÎ•º Î≥¥Ïó¨Ï§Ñ Ïàò ÏûàÏäµÎãàÎã§.
        alert('Í≤∞Í≥ºÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. ÏÑúÎ≤Ñ ÏÉÅÌÉúÎ•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
      });
    }
  };

  const restart = () => {
    setCurrentQuestion(0);
    setScores({ T: 0, E: 0 });
    setGender(null);
    setShowResult(false);
    setResultData(null);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ÌÖåÌÜ†/ÏóêÍ≤ê Ïã¨Î¶¨ ÌÖåÏä§Ìä∏</h1>
        {showResult ? (
          <>
            {resultData ? <Result result={resultData} gender={gender} /> : <p>Í≤∞Í≥ºÎ•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>}
            <button onClick={restart}>Îã§ÏãúÌïòÍ∏∞</button>
          </>
        ) : (
          questions[currentQuestion] && ( // currentQuestionÏù¥ questions.lengthÏóê ÎèÑÎã¨ÌïòÎ©¥ Î†åÎçîÎßÅÌïòÏßÄ ÏïäÎèÑÎ°ù Ï∂îÍ∞Ä
            <Question
              question={questions[currentQuestion]}
              onAnswer={handleAnswer}
            />
          )
        )}
      </header>
    </div>
  );
}

export default App;
